import {moonList} from './moonList.js'
import {Link} from 'react-router-dom'



const Shop = (props) => {

  const rerun = (card) => {
    ccard = JSON.parse(card.target.value)
    
    let index = props.cartItems.findIndex(x => x.id === parseInt(card.target))
    if (index != -1){
      let newArray = [...props.cartItems];
      let newObject = newArray[index] ;
      newObject.quantity++
      newArray[index] = newObject;
      props.updatecartItems(newArray);
      return props.cartItems
    }
    index = moonList.findIndex(x => x.id === parseInt(card.target.value))
    let newOnjectMoon = moonList[index]
    props.updatecartItems(props.cartItems.concat(newOnjectMoon))
    return props.cartItems
  // props.updatecartItems(props.cartItems.concat([1]))

  }
  
    return (

     
     
        <div className='moonContainer'>
      {moonList.map((card) => 
    <div className='card' key={card.id}> 
    <img src={card.img} alt='new' id={card.id}></img> 
    <h3>{card.name}</h3>
    <p>${card.price}/Per Square Mile</p>
    <button className='moonButton' value={JSON.stringify(card)} onClick={rerun}>Add to Cart</button>
    <Link to={`/shop/${card.id}`} className='text-link'>{card.name}</Link>
    </div>
      ) 
      }
      
      </div>
          
          
         
      
    );
  }
  export default Shop;
