import Header from './header.js'
import Home from './home.js'
import Shop from './shop.js'
import Cart from './cart.js'
import ItemPage from './itemPage.js'
import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import './App.scss';

function App() {
  const [cartItems, updatecartItems] = useState([])



    const rerun = (card) => {
      console.log(props.cartItems)
      let index = props.cartItems.findIndex(x => x.id === parseInt(card.target.value))
      if (index != -1){
        const newArray = [...props.cartItems];
        let newObject = newArray[index] ;
        newObject.quantity++
        newArray[index] = newObject;
        props.updatecartItems(newArray);
        return props.cartItems
      }
      index = moonList.findIndex(x => x.id === parseInt(card.target.value))
      let newOnjectMoon = moonList[index]
      props.updatecartItems(props.cartItems.concat(newOnjectMoon))
      return props.cartItems
    // props.updatecartItems(props.cartItems.concat([1]))

    }


  return (
    <Router>
    <div className="App">
      <Header />
      <Switch>

      <Route exact path="/">
        <Home/>
      </Route>

      <Route exact path="/shop">
        <Shop cartItems={cartItems} updatecartItems={updatecartItems}/>
      </Route>

      <Route exact path={`/shop/:subId`}>
        <ItemPage cartItems={cartItems}/>
      </Route>

      <Route exact path="/cart">
        <Cart cartItems={cartItems}/>
      </Route>

      </Switch>
    </div>
    </Router>
  );
}

export default App;
