import {useParams} from 'react-router-dom'
import {moonList} from './moonList.js'
import img1 from './images/europa.jpg';
import img2 from './images/europaBig.jpg';

const ItemPage = (props) => {
    const {subId} = useParams()
    let currentMoon = moonList[subId -1]
    let quon = 1
    const increaseQuon = () => {
        quon = quon + 1
        console.log(quon)
    }
    const decreaseQuon = () => {
        quon = quon - 1
        console.log(quon)
    }
    const addItem = () => {
        console.log(currentMoon)
        currentMoon.quantity = quon
        console.log(currentMoon)
        props.rerun(currentMoon)
    }
    
    return (
        <div className='itemContainer'>
            <div className='imageContainer'>
            <img src={img1} alt='new' ></img> 

            </div>

            <div className='description'>
                <p>
                Europa /jʊˈroʊpə/ (About this soundlisten), or Jupiter II, 
                is the smallest of the four Galilean moons orbiting Jupiter, 
                and the sixth-closest to the planet of all the 79 known moons of Jupiter. 
             

                </p>
                <div className='inputArea'>
                    <button onClick={increaseQuon}>+</button>
                    <input type='text'></input>
                    <button onClick={decreaseQuon}>-</button>


                </div>
                <button onClick={addItem}>Add to cart</button>

            </div>
            
        </div>

    );
  }
  export default ItemPage;
