import React, {useState, useEffect} from 'react';

const Cart = (props) => {
  useEffect(() => {
    let newTotal = props.cartArray.reduce( 
      ( accumulator, currentValue ) => 
      accumulator + (parseInt(currentValue.price) * currentValue.quantity), 0 )
    return props.updatetotalPrice(newTotal)
  });

    return (
      
      <div className='moonContainer'>
        <h1>{props.totalPrice}</h1>
        
      {props.cartArray.map((card) => 
    <div className='card' key={card.id}> 
    <h2>{card.quantity}</h2>
    <div className='cantsee'>hhh</div>
    <img src={card.img} alt='new' id={card.id}></img> 
    
    </div>
      ) 
      }
      
      </div>
          
         
     
    );
  }
  export default Cart;
