import React, {useState,useEffect} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import './App.scss';
import Header from './components/header.js'
import Home from './components/home.js'
import Shop from './components/shop.js'
import Cart from './components/cart.js'
import ItemPage from './components/itemPage.js'
import {moonArray} from './components/moonList.js'

function App() {
  const [cartArray, updatecartArray] = useState([])

  const [totalQuantity, updatetotalQuantity] = useState(0)



  const changecartArray = (card) => {

    let index = cartArray.findIndex(x => x.id === card.id)
    if (index !== -1){
      let newArray = [...cartArray];
      let newObject = newArray[index] ;


      newObject.quantity += card.quantity


      newArray[index] = newObject;
      return updatecartArray(newArray);
    }
    index = moonArray.findIndex(x => x.id === card.id)

    return updatecartArray(cartArray.concat(card))
}
const removeCartItem = (item) => {
  let index = cartArray.findIndex(x => x.id === card.id)
  cartArray.splice(index,1)
  return updatecartArray(cartArray)


}
useEffect(() => {
  let newQuantity = cartArray.reduce( 
    ( accumulator, currentValue ) => 
    accumulator + (currentValue.quantity), 0 )

    return updatetotalQuantity(newQuantity)
},[cartArray]);

  return (
    <Router>
    <div className="App">
      <Header totalQuantity={totalQuantity}/>
      <Switch>

      <Route exact path="/">
        <Home/>
      </Route>

      <Route exact path="/shop">
        <Shop changecartArray={changecartArray}/>
      </Route>

      <Route exact path={`/shop/:subId`}>
        <ItemPage changecartArray={changecartArray}/>
      </Route>

      <Route exact path="/cart">
        <Cart cartArray={cartArray} changecartArray={changecartArray} removeCartItem={removeCartItem}/>

      </Route>

      </Switch>
    </div>
    </Router>
  );
}

export default App;
