import {useParams} from 'react-router-dom'
import {moonArray} from './moonList.js'
import React, {useState, useEffect} from 'react';
import img1 from '../images/europa.jpg';
//import img2 from '../images/europaBig.jpg';

const ItemPage = (props) => {
    const [quon, updatequon] = useState(1)
    const {subId} = useParams()
    let currentMoon = moonArray[subId -1]
    
    const increaseQuon = () => {
        updatequon(quon + 1)
        
    }
    const decreaseQuon = () => {
        updatequon(quon - 1)
        
    }
    const addItem = () => {
        //let newObject = Object.assign({}, currentMoon);
        currentMoon.quantity = quon
        //console.log(newObject)
        props.changecartArray(currentMoon)
    }
    const handleChange = (e) => {
        console.log(e)
    }
    
    return (
        <div className='itemContainer'>
            <div className='imageContainer'>
            <img src={currentMoon.img} alt='new' ></img> 

            </div>

            <div className='description'>
                <p>
                    {currentMoon.description}
                
               </p>
               <h4>Satellite of: {currentMoon.planet}</h4>
               <h4>Square Miles: {currentMoon.sqMiles}</h4>
                <div className='inputArea'>
                <button onClick={decreaseQuon}>-</button>
                    
                    <input type='text' onChange={handleChange} value={quon}></input>
                    <button onClick={increaseQuon}>+</button>


                </div>
                <button className='add' onClick={addItem}>Add to cart</button>

            </div>
            
        </div>

    );
  }
  export default ItemPage;
