import {useParams} from 'react-router-dom'
import {moonArray} from './moonList.js'
import React, {useState} from 'react';

const ItemPage = (props) => {
    const [PurchaseQuantity, updatePurchaseQuantity] = useState(1)
    const {subId} = useParams()
    let currentMoon = moonArray[subId - 1]
    
    const increasePurchaseQuantity = () => {
        updatePurchaseQuantity(PurchaseQuantity + 1)
    }
    const decreasePurchaseQuantity = () => {
      if (PurchaseQuantity === 1){
          return PurchaseQuantity
      }
      return updatePurchaseQuantity(PurchaseQuantity - 1)
    }
    const addItem = () => {
        //let newObject = Object.assign({}, currentMoon);
        currentMoon.quantity = PurchaseQuantity
        //console.log(newObject)
        props.changecartArray(currentMoon)
    }
    const handleChange = (e) => {
        console.log(e)
    }
    
    
    return (
        
      <div className='itemContainer'>
            <div className='leftContainer'>
            <img src={currentMoon.img} alt='new' ></img> 

            </div>

            <div className='description'>
                <h1>{currentMoon.name}</h1>
                <p>
                    {currentMoon.description}
                
               </p>
               <h4>Satellite of: {currentMoon.planet}</h4>
               <h4>Surface Area: {currentMoon.sqMiles}miÂ²</h4>
                <div className='inputArea'>
                <button onClick={decreasePurchaseQuantity}>-</button>
                    
                    <input type='text' onChange={handleChange} value={PurchaseQuantity}></input>
                    <button onClick={increasePurchaseQuantity}>+</button>


                </div>
                <button className='addButton' onClick={addItem}>Add to cart</button>

            </div>
            
        </div>
        

    );
  }
  export default ItemPage;
