import Header from './header.js'
import Home from './home.js'
import Shop from './shop.js'
import Cart from './cart.js'
import img1 from './europa2.jpg';
import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import './App.scss';

function App() {
  const [cartItems, updatecartItems] = useState([{id: 1,price:100,img: img1}])

  return (
    <Router>
    <div className="App">
      <Header />
      <Switch>

      <Route exact path="/">
        <Home/>
      </Route>

      <Route path="/shop">
        <Shop cartItems={cartItems} updatecartItems={updatecartItems}/>
      </Route>

      <Route path="/cart">
        <Cart cartItems={cartItems}/>
      </Route>

      </Switch>
    </div>
    </Router>
  );
}

export default App;
