import React, {useState,useEffect} from 'react';

const Cart = (props) => {
  const [totalPrice, updatetotalPrice] = useState(0)
  
  const changeQuantity = (item,incrementer) => {
    item = JSON.parse(item.target.value)
    if (item.quantity === 1 && incrementer === -1){
      return props.removeCartItem(item)
  }
  let index = props.cartArray.findIndex(x => x.id === item.id)
  const newObject = Object.assign({}, props.cartArray[index]);
  newObject.quantity = newObject.quantity + incrementer
  props.cartArray[index] = newObject
  return props.updatecartArray(cartArray);
   
}
  
  useEffect(() => {
    
     
   let newTotal = props.cartArray.reduce( 
      ( accumulator, currentValue ) => 
      accumulator + (parseInt(currentValue.price) * currentValue.quantity), 0 )
      return updatetotalPrice(newTotal)
    
  },[totalPrice,props.cartArray]);

    return (
      <div className='cartContainer'>
        <h1>Total Price: ${totalPrice}</h1>
        {props.cartArray.map((card) => 
      
         <div className='row' key={card.id}> 
         <div className='imageContainer'>
         <img src={card.img} alt='new' id={card.id}></img> 
         </div>
         <div className='cartDescription'>
         <p className='text'>{card.name}</p>
         <p className='text'>Qantity: {card.quantity} Cost: ${card.price * card.quantity}</p>
         <div className='inputArea'>

    <button value={JSON.stringify(card)} onClick={(e) => changeQuantity (e,-1)}>-</button>
    <div className='qtext'>{card.quantity}</div>
    <button value={JSON.stringify(card)} onClick={(e) => changeQuantity (e,1)}>+</button>

   </div>
   
    </div>
    
    
    
    </div>
      ) 
      }
      
      </div>
          
         
     
    );
  }
  export default Cart;
