import {moonArray} from './moonList.js'
import {Link} from 'react-router-dom'

const Shop = (props) => {

  const AddItemToCart = (item) => {
    item = JSON.parse(item.target.value)
    item.quantity = 1
    return props.changecartArray(item)

  }
    return (
      
   <div className='shopContainer'>
     <h2>Make Your selection</h2>
     <h2>Cost is for 100 square miles</h2>
     {moonArray.map((item) => 
       <div className='card' key={item.id}>
         <h3>{item.name}</h3>

         <Link to={`/shop/${item.id}`} className='text-link'>
         <img src={item.img} alt='new'></img>
         </Link>

         <p>$ {item.price}</p>
         <button className='moonButton' value={JSON.stringify(item)} onClick={AddItemToCart}>Add to Cart</button>
         
    </div>
      ) 
      }
      </div>  
    );
  }
  export default Shop;
