import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import './App.scss';
import Header from './components/header.js'
import Home from './components/home.js'
import Shop from './components/shop.js'
import Cart from './components/cart.js'
import ItemPage from './components/itemPage.js'
import {moonList} from './components/moonList.js'

function App() {
  const [cartArray, updatecartArray] = useState([])

  const rerun = (card) => {

    console.log(cartArray)
    let index = cartArray.findIndex(x => x.id === card.id)
    if (index != -1){
      let newArray = [...cartArray];
      let newObject = newArray[index] ;
      newObject.quantity++
      newArray[index] = newObject;
      updatecartArray(newArray);
      return cartArray
    }
    index = moonList.findIndex(x => x.id === card.id)
    let newOnjectMoon = moonList[index]
    updatecartArray(cartArray.concat(newOnjectMoon))
    return cartArray
  // .updatecartArray(.cartArray.concat([1]))

  }


  return (
    <Router>
    <div className="App">
      <Header />
      <Switch>

      <Route exact path="/">
        <Home/>
      </Route>

      <Route exact path="/shop">
        <Shop cartArray={cartArray} rerun={rerun} updatecartArray={updatecartArray}/>
      </Route>

      <Route exact path={`/shop/:subId`}>
        <ItemPage cartArray={cartArray} moonList={moonList} rerun={rerun}/>
      </Route>

      <Route exact path="/cart">
        <Cart cartArray={cartArray}/>
      </Route>

      </Switch>
    </div>
    </Router>
  );
}

export default App;
