import React, {useState} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom'
import './App.scss';
import Header from './components/header.js'
import Home from './components/home.js'
import Shop from './components/shop.js'
import Cart from './components/cart.js'
import ItemPage from './components/itemPage.js'
import {moonList} from './components/moonList.js'

function App() {
  const [cartItems, updatecartItems] = useState([])



  const rerun = (card) => {

    console.log(cartItems)
    let index = cartItems.findIndex(x => x.id === card.id)
    if (index != -1){
      let newArray = [...cartItems];
      let newObject = newArray[index] ;
      newObject.quantity++
      newArray[index] = newObject;
      updatecartItems(newArray);
      return cartItems
    }
    index = moonList.findIndex(x => x.id === card.id)
    let newOnjectMoon = moonList[index]
    updatecartItems(cartItems.concat(newOnjectMoon))
    return cartItems
  // .updatecartItems(.cartItems.concat([1]))

  }


  return (
    <Router>
    <div className="App">
      <Header />
      <Switch>

      <Route exact path="/">
        <Home/>
      </Route>

      <Route exact path="/shop">
        <Shop cartItems={cartItems} rerun={rerun} updatecartItems={updatecartItems}/>
      </Route>

      <Route exact path={`/shop/:subId`}>
        <ItemPage cartItems={cartItems} moonList={moonList} rerun={rerun}/>
      </Route>

      <Route exact path="/cart">
        <Cart cartItems={cartItems}/>
      </Route>

      </Switch>
    </div>
    </Router>
  );
}

export default App;
